import "./testimonials.scss";

export default function Testimonials() {
  const data = [
    {
      id: 1,
      name: "Tom durden",
      title: "Senior developer",
      img: "assets/card-testimonials-1.jpg",
      icon: "assets/twitter.png",
      desc: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Commodi, sequi. Lorem ipsum dolor sit amet.",
    },
    {
      id: 2,
      name: "carl smith",
      title: "software developer",
      img: "assets/card-testimonials-2.jpg",
      icon: "assets/youtube.png",
      desc: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Commodi, sequi. Lorem ipsum dolor sit amet.",
      featured: true,
    },
    {
      id: 3,
      name: "ben afleck",
      title: "Senior analyst",
      img: "assets/card-testimonials3.jpg",
      icon: "assets/linkedin.png",
      desc: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Commodi, sequi. Lorem ipsum dolor sit amet.",
    },
  ];

  return (
    <div className="testimonials" id="testimonials">
      <h1>Testimonials</h1>
      <div className="container">
        {data.map((d) =>(

          <div className={d.featured ? "card featured" : "card"}>
          <div className="top">
            <img src="assets/right-arrow.png" className="left" alt="" />
            <img src={d.img} className="user" alt="" />
            <img src={d.icon} className="right" alt="" />
          </div>
          <div className="center">
           {d.desc}
          </div>
          <div className="bottom">
            <h3>{d.name}</h3>
            <h4>{d.title}</h4>
          </div>
        </div>
          ))}
      </div>
    </div>
  );
}
