import { useState } from "react";
import "./works.scss";

export default function Works() {
const [currentSlide, setcurrentSlide] = useState(0);

  const data = [
    {
      id: "1",
      icon: "./assets/mobile.png",
      title: "Web design",
      desc: " Lorem ipsum dolor sit amet consectetur adipisicing elit. Sintofficia nisi magna sit.",
      img: 'assets/att-img.jpg',
    },

    {
      id: "2",
      icon: "./assets/globe.png",
      title: "Mobile Application",
      desc: " Lorem ipsum dolor sit amet consectetur adipisicing elit. Sintofficia nisi magna sit.",
      img: 'assets/img2.jpg',
    },

    {
      id: "3",
      icon: "./assets/writing.png",
      title: "Server-Side Apps",
      desc: " Lorem ipsum dolor sit amet consectetur adipisicing elit. Sintofficia nisi magna sit.",
      img: 'assets/programming.svg',
    },
  ];

  const handleClick = (way)=>{
    way === 'left' ? setcurrentSlide(currentSlide > 0 ? currentSlide-1 : 2) :
    setcurrentSlide(currentSlide < data.length -1 ? currentSlide +1 : 0);
  }

  return (
    <div classNameName="works" id="works">
      <div classNameName="slider" style={{transform: `translateX( -${currentSlide * 100}vw) `}}>
       {data.map((d) => (

         <div classNameName="container">
          <div classNameName="item">
            <div classNameName="left">
              <div classNameName="leftContainer">
                <div classNameName="imgContainer">
                  <img src={d.icon} alt="" />
                </div>
                <h2>{d.title}</h2>
                <p>
                  {d.desc}
                </p>
                <span>Projects</span>
              </div>
            </div>
            <div classNameName="right">
              <img src="assets/att-img.jpg" alt="" />
            </div>
          </div>
        </div>
          ))}
      </div>
      <img src="assets/arrow.png" classNameName="arrow left" alt="" onClick={()=> handleClick("left")} />
      <img src="assets/arrow.png" classNameName="arrow right" alt="" onClick={()=> handleClick()} />
    </div>
  );
}
